<?php/** * Helper to Detect Admin Is Logged Or Not * @params Boolean AdminIsLogged * @params Array AdminLoginInfo - array of current user info * @return void * @author MohammadrezaEsmaeeli (info@mreweb.ir) * @license GPL * */$CI =& get_instance();$normalUserIsLogged = $CI->session->userdata('AdminIsLogged');$normalUserLoginInfo = $CI->session->userdata('AdminLoginInfo');if ($normalUserIsLogged == TRUE && count($normalUserLoginInfo) > 0) {}else {    redirect(base_url('Admin/Account/'));}?>